import {getPost,getPosts} from "@/services/posts.service";
import {filter} from "core-js/internals/array-iteration";

const mutations = {
    setPost(state,post){
    state.post = post
    },
    setPosts(state,posts){
    state.posts = posts
    },
    setPage(state,page){
      state.page = page
    },
    setPostError(state,error){
    state.postsError = error
    },
    setSearchQuery(state,searchQuery){
        state.searchQuery = searchQuery
    },
}

const actions = {
    async fetchPost({commit},id){
        try {
            const post = await  getPost(id)
            commit('setPost',post)
        } catch (err){
            commit('setPostError',err)

        }
    },
    async fetchPosts({state,commit}){
        try {
            const posts = await  getPosts()
            commit('setPosts',posts)
        } catch (err){
            commit('setPostError',err)
        }
    },
}

const getters = {
    post: ({post}) => post,
    posts: ({posts}) => posts,
    postsError: ({postsError}) => postsError,
    searchPosts(state){
        return filter(post=>post.title.toLowerCase().includes(state.searchQuery.toLowerCase()))
    }
}
const state = () => ({
    post:{},
    posts: [],
    postsError: null,
    searchQuery:'',
    page:1,
})
export default {
    mutations,
    getters,
    actions,
    state
}
